targets.expr:
sum(
    count_over_time (
        {app="opennotificaties-worker"}
        | json 
        | event="notification_successful"
        | channel_name = "zaken"
        [$__range]
    )
) or vector(0)

sum(
    count_over_time (
        {app="openformulieren-worker"}
        | json 
        | event = `registration_success` 
        | action = `registrations.main_registration`
        | plugin = "zgw-create-zaak"
        [$__range]
    )
) or vector(0)

sum(
    count_over_time (
        {app="openzaak"}
        | json 
        | event = `zaak_created` 
        [$__range]
    )
) or vector(0)

sum(
    count_over_time (
        {app="opennotificaties-worker"}
        | json 
        | event="notification_successful"
        | channel_name = "documenten"
        [$__range]
    )
) or vector(0)

sum(
    count_over_time (
        {app="objecten"}
        | json 
        | event = `object_created` 
        [$__range]
    )
) or vector(0)

sum(
    count_over_time (
        {app="opennotificaties-worker"}
        | json 
        | event="notification_successful"
        | channel_name = "objecten"
        [$__range]
    )
) or vector(0)

sum(
    count_over_time (
        {app="openformulieren-worker"}
        | json 
        | event = `registration_success` 
        | action = `registrations.main_registration`
        | plugin = `objects_api`
        [$__range]
    )
) or vector(0)

sum(
    count_over_time (
        {app="openformulieren"}
        | json 
        | event = `submission_completed` 
        [$__range]
    ) 
) or vector(0)

sum (
  count_over_time(
    {app="openformulieren-worker"}
    | json
    | event="registration_failure"
    | action="registrations.main_registration"
    [$__range]
    ) 
)
or vector(0)

sum(
    count_over_time (
        {app="openformulieren-worker"}
        | json 
        | event = `registration_success` 
        | action = `registrations.main_registration`
        [$__range]
    )
) or vector(0)

sum by(plugin, event) (
  count_over_time(
    {app="openformulieren-worker"} 
    | json 
    | event=~"registration_failure|registration_success" 
    | trigger!="on_retry"
    |__error__=``
    [$__range]
  )
)

sum by (request, code)(
    count_over_time (
        {app="openzaak"}
        | json 
        | event = "request_finished"         
        [$__range]
    )
)
